mapper.update:
update t_employee 
       set HSBC_PROJECT_NAME=#{projectName},
           SOW=#{sowNum},
           SOW_EXPIRED_DATE=#{sowExpiredDate},
           ENGAGEMENT_TYPE=#{engagementType}
  where lob=#{lob}

update t_employee
      set ROLE=#{msaRole},
          SKILL=#{skill},
          STAFF_REGION=#{staffRegion},
          BILL_RATE=#{billRate}
      where lob=#{lob}

update t_employee 
      set HSBC_SUBDEPT_ID=#{hsbcSubDept},
          HSBC_MANAGER=#{hsbcManager}
       where lob=#{lob}

update t_employee 
      set RM_USER_ID=#{nickName}
       where lob=#{lob}
