SELECT HSBC_SUBDEPT_ID 
FROM t_hsbc_dept 

	
		and HSBC_DEPT_NAME = #{hsbcDept.hsbcDeptName}
	
	
		and HSBC_SUBDEPT_NAME = #{hsbcDept.hsbcSubDeptName}

